use std::collections::HashSet;
use itertools::Itertools;

fn main() {
    let raw_stars: Vec<[usize; 2]> = INPUT.lines().enumerate()
        .flat_map(|(y, row)| row.chars().enumerate()
            .filter(|(_, ch)| *ch == '#')
            .map(move |(x, _)| [x, y])
        )
        .collect();

    {
        let stars = expand(&raw_stars, 2);

        let distance_sum: usize = stars.iter().tuple_combinations()
            .map(|(l, r)| {
                l[0].abs_diff(r[0]) + l[1].abs_diff(r[1])
            })
            .sum();

        println!("Part 1: {}", distance_sum);
    }

    {
        let stars = expand(&raw_stars, 1_000_000);

        let distance_sum: usize = stars.iter().tuple_combinations()
            .map(|(l, r)| {
                l[0].abs_diff(r[0]) + l[1].abs_diff(r[1])
            })
            .sum();

        println!("Part 2: {}", distance_sum);
    }
}

fn expand(pts: &[[usize; 2]], factor: usize) -> Vec<[usize; 2]> {
    let xs: HashSet<usize> = pts.iter().map(|p| p[0]).collect();
    let max_x = xs.iter().max().unwrap();
    let ys: HashSet<usize> = pts.iter().map(|p| p[1]).collect();
    let max_y = ys.iter().max().unwrap();

    let missing_xs: Vec<usize> = (0..*max_x).filter(|x| !xs.contains(&x)).collect();
    let missing_ys: Vec<usize> = (0..*max_y).filter(|y| !ys.contains(&y)).collect();

    pts.iter().map(|&[x, y]| {
        let dx = missing_xs.iter().filter(|mx| **mx < x).count();
        let dy = missing_ys.iter().filter(|my| **my < y).count();
        [x+dx*(factor-1), y+dy*(factor-1)]
    }).collect()
}

